import React from 'react';
import {
  Container,
  Grid,
  Typography,
  Box,
  Paper,
  Button,
  Divider,
} from '@mui/material';
import UrunKarti from '../components/urun/UrunKarti';
import { useSelector } from 'react-redux';
import { selectTumUrunler } from '../store/slices/urunlerSlice';
import { useNavigate } from 'react-router-dom';

const kategoriler = [
  {
    id: 'ana-sinif',
    isim: 'Ana Sınıfı Kitapları',
    resim: 'https://picsum.photos/300/200?random=1',
    aciklama: 'Ana sınıfı öğrencileri için özel hazırlanmış eğitici kitaplar',
  },
  {
    id: 'ilkokul',
    isim: 'İlk Okul Kitapları',
    resim: 'https://picsum.photos/300/200?random=2',
    aciklama: 'İlkokul müfredatına uygun ders ve yardımcı kitaplar',
  },
  {
    id: 'okul-oncesi',
    isim: 'Okul Öncesi Kitapları',
    resim: 'https://picsum.photos/300/200?random=3',
    aciklama: 'Okul öncesi çocukların gelişimini destekleyen kitaplar',
  },
  {
    id: 'ortaokul',
    isim: 'Orta Okul Kitapları',
    resim: 'https://picsum.photos/300/200?random=4',
    aciklama: 'Ortaokul öğrencileri için ders ve test kitapları',
  },
  {
    id: 'hobi-oyunlari',
    isim: 'Hobi Oyunları',
    resim: 'https://picsum.photos/300/200?random=5',
    aciklama: 'Eğlenceli ve eğitici hobi oyunları',
  },
  {
    id: 'okuma-kitaplari',
    isim: 'Okuma Kitapları',
    resim: 'https://picsum.photos/300/200?random=6',
    aciklama: 'Her yaş grubu için okuma kitapları',
  },
  {
    id: 'deneme-sinavlari',
    isim: 'Deneme Sınavları',
    resim: 'https://picsum.photos/300/200?random=7',
    aciklama: 'Tüm sınav türleri için deneme sınavları',
  },
  {
    id: 'sozlukler-ansiklopedi',
    isim: 'Sözlükler ve Ansiklopediler',
    resim: 'https://picsum.photos/300/200?random=8',
    aciklama: 'Kapsamlı sözlükler ve ansiklopediler',
  },
];

const ornekUrunler = [
  {
    id: '1',
    baslik: 'Matematik Seti',
    yazar: 'Ahmet Yılmaz',
    yayinevi: 'Eğitim Yayınları',
    fiyat: 49.99,
    resim: 'https://picsum.photos/300/400?random=1',
    aciklama: 'Kapsamlı matematik çalışma seti',
    puan: 4.5,
    stok: 50
  },
  {
    id: '2',
    baslik: 'Türkçe Kitabı',
    yazar: 'Ayşe Demir',
    yayinevi: 'Bilgi Yayınları',
    fiyat: 39.99,
    resim: 'https://picsum.photos/300/400?random=2',
    aciklama: 'Türkçe dil bilgisi ve okuma kitabı',
    puan: 4.8,
    stok: 30
  },
  {
    id: '3',
    baslik: 'Fen Bilimleri',
    yazar: 'Mehmet Kaya',
    yayinevi: 'Fen Yayınları',
    fiyat: 54.99,
    resim: 'https://picsum.photos/300/400?random=3',
    aciklama: 'İlköğretim fen bilimleri kitabı',
    puan: 4.6,
    stok: 25
  },
  {
    id: '4',
    baslik: 'İngilizce Seti',
    yazar: 'John Smith',
    yayinevi: 'Dil Yayınları',
    fiyat: 69.99,
    resim: 'https://picsum.photos/300/400?random=4',
    aciklama: 'Temel seviye İngilizce eğitim seti',
    puan: 4.7,
    stok: 40
  }
];

const KategoriKarti = ({ kategori }) => {
  const navigate = useNavigate();

  return (
    <Paper 
      sx={{ 
        height: '100%', 
        display: 'flex', 
        flexDirection: 'column',
        cursor: 'pointer',
        '&:hover': {
          transform: 'translateY(-4px)',
          boxShadow: 3,
          transition: 'all 0.3s ease-in-out'
        }
      }}
      onClick={() => navigate(`/kategori/${kategori.id}`)}
    >
      <Box
        component="img"
        src={kategori.resim}
        alt={kategori.isim}
        sx={{
          width: '100%',
          height: 140,
          objectFit: 'cover',
        }}
      />
      <Box sx={{ p: 2, flexGrow: 1 }}>
        <Typography variant="h6" gutterBottom>
          {kategori.isim}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          {kategori.aciklama}
        </Typography>
      </Box>
    </Paper>
  );
};

const Anasayfa = () => {
  const urunler = useSelector(selectTumUrunler);
  const gosterilecekUrunler = urunler.length > 0 ? urunler : ornekUrunler;

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 8 }}>
      {/* Hero Section */}
      <Paper
        sx={{
          p: 4,
          mb: 4,
          backgroundImage: 'linear-gradient(45deg, #2196F3 30%, #21CBF3 90%)',
          color: 'white',
          borderRadius: 2,
        }}
      >
        <Typography variant="h3" gutterBottom>
          Hoş Geldiniz!
        </Typography>
        <Typography variant="h6" sx={{ mb: 2 }}>
          Eğitim ve öğrenme yolculuğunuzda yanınızdayız.
        </Typography>
        <Button variant="contained" color="secondary" size="large">
          Keşfetmeye Başla
        </Button>
      </Paper>

      {/* Kategoriler */}
      <Typography variant="h4" gutterBottom>
        Kategoriler
      </Typography>
      <Grid container spacing={3} sx={{ mb: 6 }}>
        {kategoriler.map((kategori) => (
          <Grid item xs={12} sm={6} md={3} key={kategori.id}>
            <KategoriKarti kategori={kategori} />
          </Grid>
        ))}
      </Grid>

      <Divider sx={{ my: 6 }} />

      {/* Öne Çıkan Ürünler */}
      <Box sx={{ mb: 6 }}>
        <Typography variant="h4" gutterBottom>
          Öne Çıkan Ürünler
        </Typography>
        <Grid container spacing={3}>
          {gosterilecekUrunler.map((urun) => (
            <Grid item xs={12} sm={6} md={3} key={urun.id}>
              <UrunKarti urun={urun} />
            </Grid>
          ))}
        </Grid>
      </Box>

      <Divider sx={{ my: 6 }} />

      {/* Yeni Çıkan Kitaplar */}
      <Box sx={{ mb: 6 }}>
        <Typography variant="h4" gutterBottom>
          Yeni Çıkan Kitaplar
        </Typography>
        <Grid container spacing={3}>
          {gosterilecekUrunler.slice(0, 4).map((urun) => (
            <Grid item xs={12} sm={6} md={3} key={urun.id}>
              <UrunKarti urun={urun} />
            </Grid>
          ))}
        </Grid>
      </Box>
    </Container>
  );
};

export default Anasayfa;
