import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import {
  Card,
  CardMedia,
  CardContent,
  Typography,
  CardActions,
  Button,
  IconButton,
  Box,
  Rating,
} from '@mui/material';
import { ShoppingCart, Favorite } from '@mui/icons-material';
import { addToCart } from '../../store/slices/cartSlice';
import { toggleFavorite, selectIsFavorite } from '../../store/slices/favoritesSlice';

const UrunKarti = ({ urun = {} }) => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const isFavorite = useSelector(state => selectIsFavorite(state, urun?.id));

  if (!urun || !urun.id) {
    return null;
  }

  const handleAddToCart = () => {
    dispatch(addToCart(urun));
  };

  const handleToggleFavorite = () => {
    dispatch(toggleFavorite(urun));
  };

  const handleClick = () => {
    navigate(`/urun/${urun.id}`);
  };

  return (
    <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
      <CardMedia
        component="img"
        height="200"
        image={urun.resim}
        alt={urun.baslik}
        sx={{ objectFit: 'contain', cursor: 'pointer' }}
        onClick={handleClick}
      />
      <CardContent sx={{ flexGrow: 1 }}>
        <Typography 
          gutterBottom 
          variant="h6" 
          component="h2" 
          sx={{ 
            cursor: 'pointer',
            '&:hover': { color: 'primary.main' }
          }}
          onClick={handleClick}
        >
          {urun.baslik}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          {urun.yazar}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          {urun.yayinevi}
        </Typography>
        <Box sx={{ display: 'flex', alignItems: 'center', mt: 1 }}>
          <Rating value={urun.puan} readOnly size="small" precision={0.5} />
          <Typography variant="body2" color="text.secondary" sx={{ ml: 1 }}>
            ({urun.puan}/5)
          </Typography>
        </Box>
        <Typography variant="h6" color="primary" sx={{ mt: 1 }}>
          {urun.fiyat.toFixed(2)} TL
        </Typography>
      </CardContent>
      <CardActions>
        <Button
          size="small"
          variant="contained"
          startIcon={<ShoppingCart />}
          onClick={handleAddToCart}
          fullWidth
        >
          Sepete Ekle
        </Button>
        <IconButton
          size="small"
          color={isFavorite ? 'secondary' : 'default'}
          onClick={handleToggleFavorite}
        >
          <Favorite />
        </IconButton>
      </CardActions>
    </Card>
  );
};

export default UrunKarti;
