import React from 'react';
import { Container, Grid, Typography, Card, CardContent, CardMedia, Button, Box } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const kategoriler = [
  { id: 1, isim: 'Ana Sınıfı Kitapları', resim: 'yolEklenecek' },
  { id: 2, isim: 'İlk Okul Kitapları', resim: 'yolEklenecek' },
  { id: 3, isim: 'Okul Öncesi Kitapları', resim: 'yolEklenecek' },
  { id: 4, isim: 'Orta Okul Kitapları', resim: 'yolEklenecek' },
  { id: 5, isim: 'Hobi Oyunları', resim: 'yolEklenecek' },
  { id: 6, isim: 'Okuma Kitapları', resim: 'yolEklenecek' },
  { id: 7, isim: 'Deneme Sınavları', resim: 'yolEklenecek' },
  { id: 8, isim: 'Sözlükler ve Ansiklopediler', resim: 'yolEklenecek' },
];

const Anasayfa = () => {
  const navigate = useNavigate();

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      <Typography variant="h4" gutterBottom>
        Kategoriler
      </Typography>
      <Grid container spacing={3}>
        {kategoriler.map((kategori) => (
          <Grid item xs={12} sm={6} md={3} key={kategori.id}>
            <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
              <CardMedia
                component="img"
                height="140"
                image={kategori.resim}
                alt={kategori.isim}
              />
              <CardContent sx={{ flexGrow: 1 }}>
                <Typography gutterBottom variant="h6" component="div">
                  {kategori.isim}
                </Typography>
                <Button 
                  variant="contained" 
                  color="primary" 
                  fullWidth
                  onClick={() => navigate(`/kategori/${kategori.id}`)}
                >
                  İncele
                </Button>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      <Box sx={{ mt: 4 }}>
        <Typography variant="h4" gutterBottom>
          Öne Çıkan Kitaplar
        </Typography>
        <Grid container spacing={3}>
          {/* Öne çıkan kitaplar buraya eklenecek */}
        </Grid>
      </Box>

      <Box sx={{ mt: 4 }}>
        <Typography variant="h4" gutterBottom>
          Yeni Çıkan Kitaplar
        </Typography>
        <Grid container spacing={3}>
          {/* Yeni çıkan kitaplar buraya eklenecek */}
        </Grid>
      </Box>
    </Container>
  );
};

export default Anasayfa;
