import React from 'react';
import {
  Container,
  Grid,
  Typography,
  Box,
} from '@mui/material';
import UrunKarti from '../components/urun/UrunKarti';
import { useSelector } from 'react-redux';
import { selectTumUrunler } from '../store/slices/urunlerSlice';

const ornekUrunler = [
  {
    id: '1',
    baslik: 'Matematik Seti',
    yazar: 'Ahmet Yılmaz',
    yayinevi: 'Eğitim Yayınları',
    fiyat: 49.99,
    resim: 'https://picsum.photos/300/400?random=1',
    aciklama: 'Kapsamlı matematik çalışma seti',
    puan: 4.5,
    stok: 50
  },
  {
    id: '2',
    baslik: 'Türkçe Kitabı',
    yazar: 'Ayşe Demir',
    yayinevi: 'Bilgi Yayınları',
    fiyat: 39.99,
    resim: 'https://picsum.photos/300/400?random=2',
    aciklama: 'Türkçe dil bilgisi ve okuma kitabı',
    puan: 4.8,
    stok: 30
  },
  {
    id: '3',
    baslik: 'Fen Bilimleri',
    yazar: 'Mehmet Kaya',
    yayinevi: 'Fen Yayınları',
    fiyat: 54.99,
    resim: 'https://picsum.photos/300/400?random=3',
    aciklama: 'İlköğretim fen bilimleri kitabı',
    puan: 4.6,
    stok: 25
  },
  {
    id: '4',
    baslik: 'İngilizce Seti',
    yazar: 'John Smith',
    yayinevi: 'Dil Yayınları',
    fiyat: 69.99,
    resim: 'https://picsum.photos/300/400?random=4',
    aciklama: 'Temel seviye İngilizce eğitim seti',
    puan: 4.7,
    stok: 40
  }
];

const Anasayfa = () => {
  const urunler = useSelector(selectTumUrunler);
  const gosterilecekUrunler = urunler.length > 0 ? urunler : ornekUrunler;

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Box sx={{ mb: 4 }}>
        <Typography variant="h4" gutterBottom>
          Kitaplarımız
        </Typography>
        <Grid container spacing={4}>
          {gosterilecekUrunler.map((urun) => (
            <Grid item key={urun.id} xs={12} sm={6} md={3}>
              <UrunKarti urun={urun} />
            </Grid>
          ))}
        </Grid>
      </Box>
    </Container>
  );
};

export default Anasayfa;
